#!/bin/sh

# 显示欢迎词
clear
echo "欢迎使用Zag的软件源自动安装脚本。本脚本旨在方便大家替换光影猫官方固件和QWRT固件的内置软件源，以实现增加插件选择和更早获取插件更新。"
echo "当前脚本支持的固件有QWRT-R24.04.06 for Photonicat，PhotonicatWrt-23.10-r6311。"
echo "脚本会替换固件内置软件源，请自行斟酌。"
echo "是否继续？(Y/N)"
read -r CONTINUE
if [ "$CONTINUE" != "Y" ] && [ "$CONTINUE" != "y" ]; then
    echo "脚本已退出。"
    exit 0
fi

# 检查固件是否匹配
check_firmware() {
    echo "正在检查固件是否匹配..."
    local current_kernel_version=$(grep "Depends: kernel" /usr/lib/opkg/status | sed -e 's/.*kernel (= \([^-]*\).*/\1/' | head -n 1)
    local current_vermagic=$(grep "Depends: kernel" /usr/lib/opkg/status | sed -e 's/.*-\([^-]*\)).*/\1/' | head -n 1)
    
    # 这里需要替换为预设的内核版本和vermagic值
    # 第一组预期值
    local expected_kernel_version1="6.1.82"
    local expected_vermagic1="ffa0de838ea803eeb72a9b1192db7d02"
    # 第二组预期值
    local expected_kernel_version2="5.15.153"
    local expected_vermagic2="a842d3a5487c87d5de80c76270f0e569"
    # 第三组预期值
    local expected_kernel_version3="6.1.56"
    local expected_vermagic3="1546315ff771cec8bfe79b86c935cc87"

    if [ "$current_kernel_version" = "$expected_kernel_version1" ] && [ "$current_vermagic" = "$expected_vermagic1" ]; then
        echo "当前固件是ZagWRT for X86"
        modify_sources 1
    elif [ "$current_kernel_version" = "$expected_kernel_version2" ] && [ "$current_vermagic" = "$expected_vermagic2" ]; then
        echo "当前固件是QWRT-R24.04.06 for Photonicat"
        modify_sources 2
    elif [ "$current_kernel_version" = "$expected_kernel_version3" ] && [ "$current_vermagic" = "$expected_vermagic3" ]; then
        echo "当前固件是PhotonicatWrt-23.10-r6311"
        modify_sources 3
    else
        echo "当前固件与已知软件源不匹配，请检查固件来源是否为官方，你可以在https://pcat.qsim.top/firmware/下载到最新固件，或者联系脚本作者提供你所使用固件的内核版本和vermagic。"
        return 1
    fi
}

# 修改软件源
modify_sources() {
    echo "正在修改软件源..."
    case $1 in
        1)
            cat > /etc/opkg/customfeeds.conf << EOF
            src/gz zagwrt_core https://pcat.qsim.top/openwrt/x86/targets/x86/64/packages
            src/gz zagwrt_base https://pcat.qsim.top/openwrt/x86/packages/x86_64/base
            src/gz zagwrt_kiddin9 https://pcat.qsim.top/openwrt/x86/packages/x86_64/kiddin9
            src/gz zagwrt_luci https://pcat.qsim.top/openwrt/x86/packages/x86_64/luci
            src/gz zagwrt_nuexini https://pcat.qsim.top/openwrt/x86/packages/x86_64/nuexini
            src/gz zagwrt_openai https://pcat.qsim.top/openwrt/x86/packages/x86_64/openai
            src/gz zagwrt_packages https://pcat.qsim.top/openwrt/x86/packages/x86_64/packages
            src/gz zagwrt_routing https://pcat.qsim.top/openwrt/x86/packages/x86_64/routing
            src/gz zagwrt_sirpdboy https://pcat.qsim.top/openwrt/x86/packages/x86_64/sirpdboy
            src/gz zagwrt_telephony https://pcat.qsim.top/openwrt/x86/packages/x86_64/telephony
            src/gz zagwrt_video https://pcat.qsim.top/openwrt/x86/packages/x86_64/video

EOF
            ;;
        2)
            cat > /etc/opkg/customfeeds.conf << EOF
            src/gz zag_core https://pcat.qsim.top/qwrt/targets/rockchip/armv8/packages
            src/gz zag_base https://pcat.qsim.top/qwrt/packages/aarch64_generic/base
            src/gz zag_kiddin9 https://pcat.qsim.top/qwrt/packages/aarch64_generic/kiddin9
            src/gz zag_luci https://pcat.qsim.top/qwrt/packages/aarch64_generic/luci
            src/gz zag_nuexini https://pcat.qsim.top/qwrt/packages/aarch64_generic/nuexini
            src/gz zag_openai https://pcat.qsim.top/qwrt/packages/aarch64_generic/openai
            src/gz zag_packages https://pcat.qsim.top/qwrt/packages/aarch64_generic/packages
            src/gz zag_routing https://pcat.qsim.top/qwrt/packages/aarch64_generic/routing
            src/gz zag_sirpdboy https://pcat.qsim.top/qwrt/packages/aarch64_generic/sirpdboy
            src/gz zag_telephony https://pcat.qsim.top/qwrt/packages/aarch64_generic/telephony

EOF
            ;;
        3)
            cat > /etc/opkg/customfeeds.conf << EOF
            src/gz zag-photonicat_core https://pcat.qsim.top/zag/targets/rockchip/armv8/packages
            src/gz zag-photonicat_base https://pcat.qsim.top/zag/packages/aarch64_generic/base
            src/gz zag-photonicat_kiddin9 https://pcat.qsim.top/zag/packages/aarch64_generic/kiddin9
            src/gz zag-photonicat_lean_luci https://pcat.qsim.top/zag/packages/aarch64_generic/lean_luci
            src/gz zag-photonicat_lean_packages https://pcat.qsim.top/zag/packages/aarch64_generic/lean_packages
            src/gz zag-photonicat_luci https://pcat.qsim.top/zag/packages/aarch64_generic/luci
            src/gz zag-photonicat_nuexini https://pcat.qsim.top/zag/packages/aarch64_generic/nuexini
            src/gz zag-photonicat_openai https://pcat.qsim.top/zag/packages/aarch64_generic/openai
            src/gz zag-photonicat_packages https://pcat.qsim.top/zag/packages/aarch64_generic/packages
            src/gz zag-photonicat_routing https://pcat.qsim.top/zag/packages/aarch64_generic/routing
            src/gz zag-photonicat_sirpdboy https://pcat.qsim.top/zag/packages/aarch64_generic/sirpdboy
            src/gz zag-photonicat_telephony https://pcat.qsim.top/zag/packages/aarch64_generic/telephony

EOF
            ;;
    esac        
    echo "软件源修改完成。是否现在更新软件源？(Y/N)，按回车返回主菜单。"
    read -r UPDATE_SOURCES
    if [ "$UPDATE_SOURCES" = "Y" ] || [ "$UPDATE_SOURCES" = "y" ]; then
        update_sources
    elif [ "$UPDATE_SOURCES" = "N" ] || [ "$UPDATE_SOURCES" = "n" ]; then
        echo "脚本已退出。"
    exit 0
    elif [ -z "$UPDATE_SOURCES" ]; then
        main_menu
fi
}

# 更新软件源
update_sources() {
    echo "正在更新软件源..."
    echo "nameserver 114.114.114.114">/tmp/resolv.conf && rm -f /var/lock/opkg.lock && opkg update
    echo "软件源更新完成。是否现在进行固件个性化配置？(Y/N)，按回车返回主菜单。"
    read -r CUSTOMIZE_FIRMWARE
    if [ "$CUSTOMIZE_FIRMWARE" = "Y" ] || [ "$CUSTOMIZE_FIRMWARE" = "y" ]; then
        customize_firmware_menu
    elif [ "$CUSTOMIZE_FIRMWARE" = "N" ] || [ "$CUSTOMIZE_FIRMWARE" = "n" ]; then
        echo "脚本已退出。"
    exit 0
    elif [ -z "$CUSTOMIZE_FIRMWARE" ]; then
        main_menu
fi
}

