            while [ $attempt -le $max_attempts ]; do
                echo "安装尝试 #$attempt"
                all_installed=true
                for pkg in $packages_3; do
                    # 检查软件包是否已安装
                    opkg list-installed | grep -q "^$pkg "
                    if [ $? -eq 0 ]; then
                        echo "$pkg 已安装"
                    else
                        echo "$pkg 未安装，尝试安装..."
                        opkg install $pkg
                        if [ $? -eq 0 ]; then
                            echo "$pkg 安装成功"
                        else
                            echo "$pkg 安装失败"
