# 主菜单
main_menu() {
    clear
    echo "请选择以下操作："
    echo "1. 修改软件源"
    echo "2. 更新软件源（建议完成1后执行）"
    echo "3. 个性化固件（建议完成2后执行，一键安装常用插件，请全程确保网络连接稳定，脚本还在完善，慎用，建议开始前做好备份）"
    echo "按N直接退出脚本"
    read -r CHOICE
    case $CHOICE in
        1)
            check_firmware
            ;;
        2)
            update_sources
            ;;
        3)
            customize_firmware_menu
            ;;
        N|n)
            echo "脚本已退出。"
            exit 0
            ;;
        *)
            echo "无效输入，请重新选择。"
            main_menu
            ;;
    esac
}

# 二级菜单
customize_firmware_menu() {
    clear
    echo "选择要个性化的固件版本：（请再三确认你的固件是否匹配，软件源是否已完成修改。操作有风险请做好备份）"
    echo "1. ZagWrt"
    echo "2. QWRT-R24.04.06 for Photonicat"
    echo "3. PhotonicatWrt-23.10-r6311"
    echo "4. 返回主菜单"
    echo "请输入选项并回车确认："
    read -r FIRMWARE_CHOICE
    case "$FIRMWARE_CHOICE" in
        1) customize_firmware 1 ;;
        2) customize_firmware 2 ;;
        3) customize_firmware 3 ;;
        4) main_menu ;;
        *) echo "无效输入，请重新输入。"; customize_firmware_menu ;;
    esac
}


# 执行主菜单函数
main_menu

